import { GET_POSTS, LIKE_POST } from "../actions/post.actions";

const initialState = {};

export default function postReducer(state = initialState, action) {
  switch (action.type) {
    case GET_POSTS:
      return action.playload;
    case LIKE_POST:
      return state.map((post) => {});
    default:
      return state;
  }
}
