import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { NavLink } from "react-router-dom";
import { isEmpty } from "../../Util/functions";

const NewPostForm = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [message, setMessage] = useState("");
  const [picture, setPicture] = useState(null);
  const [video, setVideo] = useState("");
  const [file, setFile] = useState();
  const userData = useSelector((state) => state.userReducer);

  const handlePicture = () => {};

  useEffect(() => {
    if (!isEmpty(userData)) {
      setIsLoading(false);
    }
  }, [userData]);

  return (
    <div className="post-container">
      {isLoading ? (
        <i className="fas fa-spinner fa-pulse" />
      ) : (
        <>
          <div className="data">
            <p>
              <span>
                {userData.followings ? userData.followings.length : 0}
              </span>{" "}
              {userData.followings && userData.followings.length > 1
                ? "abonnements"
                : "abonnement"}
            </p>
            <p>
              {userData.followers ? userData.followers.length : 0}
              <span>
                {" "}
                {userData.followers && userData.followers.length > 1
                  ? "abonnés"
                  : "abonnés"}
              </span>
            </p>
          </div>
          <NavLink exact to="/profil">
            <div className="user-info">
              <img src={userData.picture} alt="userPic" />
            </div>
          </NavLink>
          <div className="post-form">
            <textarea
              name="message"
              id="message"
              placeholder="Quoi de neuf ?"
              onChange={(event) => setMessage(event.target.value)}
              value={message}
            />
          </div>
          <div className="footer-form">
            {isEmpty(video) && (
              <>
                <img src="./img/icons/picture.svg" alt="picIcon" />
                <input
                  type="file"
                  id="file-upload"
                  name="file"
                  accept=".jpg, .jpeg, .png"
                  onChange={(event) => handlePicture(event)}
                ></input>
              </>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default NewPostForm;
